  cambioArchivo(event) {
    if (event.files.length > 0) {
      for (let i = 0; i < event.files.length; i++) {
        this.mensajeArchivo = `Archivo preparado: ${event.files[i].name}`;
        this.nombreArchivo = event.files[i].name;
